    private void btAcaoActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btAcaoActionPerformed
       lbTexto1.setText("");
       
       Double casquinha = Double.valueOf(tfValor1.getText()) * 2;
       Double sundae = Double.valueOf(tfValor1.getText()) * 5;
       Double milkshake = Double.valueOf(tfValor1.getText()) * 7;
       
       String texto = String.format("Valor a pagar: R$%.2f",(casquinha+sundae+milkshake));
       
       
       lbTexto1.setText(texto);
    }//GEN-LAST:event_btAcaoActionPerformed

